void
	check_int
	(int p1, int p2)
{
	if (p1 == p2)
		ft_printf("\033[0;32mOK\033[0m\n");
	else
		ft_printf("\033[1;31mKO\033[0m\n");
}

void
	set_map_pos
	(t_process *p, int pos, unsigned char par)
{
	while (pos >= MEM_SIZE)
		pos -= MEM_SIZE;
	*(p->map + pos) = par;
}

void
	set_map_params
	(t_process *p, int start_pos, int size1, int par1, int size2, int par2, int par3)
{
	set_map_pos(p, start_pos, par1);
	set_map_pos(p, start_pos + size1, par2);
	set_map_pos(p, start_pos + size1 + size2, par3);
}

int
	check_me
(t_process *p, int pc, int expected)
{
	int				result;

	while (pc >= MEM_SIZE)
		pc -= MEM_SIZE;
	p->pc = p->map + pc;
	result = op_check(p);
	ft_printf("check\t");
	check_int(expected, result);
	return (1);
}

t_process
	init_process
	(void)
{
	t_process	p;
	
	p.map = test_map;
	p.pc = p.map;
	return (p);
}
